from binary_substrings.quest import countBinarySubstrings


def test_countBinarySubstrings_1():
    result = countBinarySubstrings("11000110")
    assert result == 5


def test_countBinarySubstrings_2():
    result = countBinarySubstrings("100110")
    assert result == 4


def test_countBinarySubstrings_3():
    result = countBinarySubstrings(
        ""
    )
    assert result == 3336
